import React, { useContext, useState } from "react";
import socketService from "../services/socketService";
import gameService from "../services/gameService";

interface IJoinRoomProps {}

export function JoinRoom(props: IJoinRoomProps) {
  const [roomName, setRoomName] = useState("");
  const [isJoining, setJoining] = useState(false);
  const [isCreating, setCreating] = useState(false);
  const [isCreator, setCreator] = useState(true);
  const [isSelected, setSelected] = useState(false);

  const handleRoomNameChange = (e: React.ChangeEvent<any>) => {
    const value = e.target.value;
    setRoomName(value);
  }

  const handleCreateSelected = () => {
    setSelected(true);
    setCreator(true);
  }

  const handleJoinSelected = () => {
    setSelected(true);
    setCreator(false);
  }

  const createOrJoinRoom = async (e: React.FormEvent) => {
    e.preventDefault();

    if (isCreator) {
      createRoom()
    } else {
      joinRoom()
    }

  }

  const createRoom = async () => {
    const socket = socketService.socket;
    if (!roomName || roomName.trim() === "" || !socket) return;

    setCreating(true);

    const joined = await gameService
      .createGameRoom(socket, roomName)
      .catch((err) => {
        alert(err);
      });

    setCreating(false);
  }

  const joinRoom = async () => {
    const socket = socketService.socket;
    if (!roomName || roomName.trim() === "" || !socket) return;

    setJoining(true);

    const joined = await gameService
      .joinGameRoom(socket, roomName)
      .catch((err) => {
        alert(err);
      });

    setJoining(false);
  }

  return (
    <div>
      {!isSelected ? (
        <div>
          <button onClick={() => handleCreateSelected()}>Create Lobby</button>
          <button onClick={() => handleJoinSelected()}>Join Lobby</button>
        </div>
      ) : (
        <div>
          <form onSubmit={createOrJoinRoom}>
            <div>
              <h4>Room Name:</h4>
              <input placeholder="Room ID" value={roomName} onChange={handleRoomNameChange}/>
              <button type="submit" disabled={isJoining || isCreating}>
                {isCreator ? 
                  (isCreating ? "Creating..." : "Create") :
                  (isJoining ? "Joining..." : "Join")}
              </button>
            </div>
          </form>
        </div>
      )}


    </div>

  );

}
