import React, {useContext, useEffect, useState} from 'react';
import gameContext from '../gameContext';
import gameService from '../services/gameService';
import socketService from '../services/socketService';
import { ServerEvents } from "../shared/types/serverEvents";
import { ServerPayloads } from "../shared/types/serverPayloads";

const initialGameState: ServerPayloads[ServerEvents.LobbyState] = {
  lobbyId: '',
  isStarted: false,
  isFinished: false,
  numPlayers: 0,
  currentPigIndex1: 0,
  currentPigIndex2: 0,
  currentRollScore: 0,
  currentTurnPlayer: '',
  currentTurnScore: 0,
  scores: {}
}

export function Game() {

  const [gameState, setGameState] = useState(initialGameState)

  const { setPlayerTurn, isPlayerTurn, setGameStarted, isGameStarted } = useContext(gameContext);

  const handleRollButton = () => {
    if (socketService.socket)
      gameService.updateGame(socketService.socket, 'roll');
  }

  const handlePassButton = () => {
    if (socketService.socket)
      gameService.updateGame(socketService.socket, 'pass');
  }

  const handleGameUpdate = () => {
    if (socketService.socket) {
      gameService.onGameUpdate(socketService.socket, (newGameState) => {
        setGameState(newGameState);
        console.log(newGameState);
        const isTurn = newGameState.currentTurnPlayer === socketService.socket!.id
        setPlayerTurn(isTurn);
      });
    }
  }

  const handleGameStart = () => {
    if (socketService.socket)
      gameService.onStartGame(socketService.socket, () => {
        setGameStarted(true);
      });
  }

  useEffect(() => {
    handleGameUpdate();
    handleGameStart();
  }, []);

  return (

    <div>
      {!isGameStarted ? 
        (<h2>Waiting for other players...</h2>) :
        (
        <div>
          <button onClick={() => handlePassButton()}>Pass</button>
          <button onClick={() => handleRollButton()}>Roll</button>
          <div>
            <p>Pig 1: {gameState.currentPigIndex1}</p>
            <p>Pig 2: {gameState.currentPigIndex2}</p>
            <p>Roll score: {gameState.currentRollScore}</p>
            <p>Turn score: {gameState.currentTurnScore}</p>
            <p>{gameState.scores[gameState.currentTurnPlayer].name}'s turn, 
            score: {gameState.scores[gameState.currentTurnPlayer].score}</p>

          </div>
          <div>
            {Object.entries(gameState.scores).map(([key, value]) => (
              <div key={key}>
                Key: {key}, Player: {value.name}, Score: {value.score}
              </div>
            ))}
          </div>
        </div>
        )}

    </div>
  );


}
